import {
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { useState } from "react"
import IconButton from '@material-ui/core/IconButton';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { styled } from "@material-ui/core"


const GridViewSettings = ({props, disabled }: any) => {
  const [value, setValue] = useState({startAt: 0})

  const handleClick = (side: string) => {
    if(side === "left") {
      setValue({startAt: value.startAt + props.incrementAmt})
      Streamlit.setComponentValue(value.startAt)
    } else if (side === "right") {
      setValue({startAt: value.startAt - props.incrementAmt})
      Streamlit.setComponentValue(value.startAt)
    }
  }


  const Arrow = styled(ArrowForwardIosIcon)({
  })
  const FlippedArrow = styled(ArrowForwardIosIcon)({
    transform: "rotate(180deg)"
  })
  const FixedMarginIconButton = styled(IconButton)({
    marginBottom:"10px"
  })

  return(
    "Hello"
  /*<div style={{width:"100%"}}>
    <div style={{position: "relative", left: "50%", display: "block", marginLeft: "auto", marginRight:"auto"}}>
      <FixedMarginIconButton color="primary"
                             disabled={disabled}
                             onClick={() => handleClick("left")}
      >
        <FlippedArrow fontSize="large"/>
      </FixedMarginIconButton>
      <FixedMarginIconButton color="primary"
                             disabled={disabled}
                             onClick={() => handleClick("right")}
      >
        <Arrow fontSize="large"/>
      </FixedMarginIconButton>
    </div>
  </div>*/
  )
}

