import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode, useEffect} from "react"
import IconButton from '@material-ui/core/IconButton';
import { makeStyles } from '@material-ui/core/styles';
import DeleteIcon from '@material-ui/icons/Delete';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import KeyboardVoiceIcon from '@material-ui/icons/KeyboardVoice';
import Icon from '@material-ui/core/Icon';
import SaveIcon from '@material-ui/icons/Save';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { red } from "@material-ui/core/colors"
import { ArrowBackIos } from "@material-ui/icons"
import { styled } from "@material-ui/core"

let state = "idle"

const GridViewSettings = ({args, disabled }: any) => {
  useEffect(() => {
    Streamlit.setFrameHeight()

    // Button was just clicked, and component code reruns
    // But we don't want to set component value to false yet!
    if (state === "clicked") {
      state = "reset"
    }

      // A rerun was done, executing the component code again
    // So we can safely set the component value to False (which triggers another rerun)
    else if (state === "reset") {
      Streamlit.setComponentValue({left: false, right: false})
      state = "idle"
    }
  })

  const handleClick = (side: string) => {
    if(side === "left") {
      Streamlit.setComponentValue({ left: true, right: false })
    } else if (side === "right") {
      Streamlit.setComponentValue({ left: false, right: true })
    }
    console.log('Hello')
    state = "clicked"
  }


  const Arrow = styled(ArrowForwardIosIcon)({
  })
  const FlippedArrow = styled(ArrowForwardIosIcon)({
    transform: "rotate(180deg)"
  })
  const FixedMarginIconButton = styled(IconButton)({
    marginBottom:"10px"
  })

  return(
  <div style={{width:"100%", }}>
    <FixedMarginIconButton color="primary"
                           disabled={disabled}
                           onClick={() => handleClick("left")}
    >
      <FlippedArrow fontSize="large"/>
    </FixedMarginIconButton>
    <FixedMarginIconButton color="primary"
                           disabled={disabled}
                           onClick={() => handleClick("right")}
    >
      <Arrow fontSize="large"/>
    </FixedMarginIconButton>
  </div>
  )
}

